import {
  REGISTER,
  REGISTER_SUCCESS,
  REGISTER_FAILURE,
  LOGIN,
  LOGIN_SUCCESS,
  LOGIN_FAILURE,
  AUTHENTICATE,
  DEAUTHENTICATE,
  USER
} from '../types';

const initialState = {
  token: null,
  user: null,
  register_pending: false,
  register_success: null,
  register_error: null,
  login_pending: false,
  login_error: null
};

export default (state = initialState, action) => {
  switch (action.type) {
    case REGISTER:
      return {
        ...state,
        register_pending: true,
        register_error: null
      }
    case REGISTER_SUCCESS:
      return {
        ...state,
        register_pending: false,
        register_success: action.payload,
        register_error: null
      }
    case REGISTER_FAILURE:
      return {
        ...state,
        register_pending: false,
        register_error: action.error
      }
    case LOGIN:
      return {
        ...state,
        login_pending: true,
        login_error: null
      }
    case LOGIN_SUCCESS:
      return {
        ...state,
        login_pending: false,
        token: action.payload,
        login_error: null
      }
    case LOGIN_FAILURE:
      return {
        ...state,
        login_pending: false,
        login_error: action.error
      }
    case AUTHENTICATE:
      return {
        ...state,
        token: action.payload
      }
    case DEAUTHENTICATE:
      return {
        ...state,
        token: null
      };
    default:
      return state;
  }
};
